import Header from "../components/Header";
import { experience } from "../constants";

const Experience = () => {
  return (
    <main className="bg-green-400">
      <Header />
      <div className="text-slate-100">
        <div className="absolute right-20 top-10 flex flex-col justify-center h-screen">
          <ul className="text-right">
            {experience.map((job) => (
              <li key={job.id} className="mb-10 bg-blue-500">
                <h1 className="mb-2">
                  <span className="text-2xl font-semibold">{job.title}, </span>
                  <span className="text-xl font-thin">{job.company}</span>
                </h1>
                <p>{job.summary}</p>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </main>
  );
};

export default Experience;
